function startCountdown(targetDate) {
    function updateTimer() {
        let now = new Date().getTime();
        let timeLeft = targetDate - now;

        if (timeLeft < 0) {
            document.getElementById("days").innerText = "000";
            document.getElementById("hours").innerText = "00";
            document.getElementById("minutes").innerText = "00";
            document.getElementById("seconds").innerText = "00";
            clearInterval(interval);
            return;
        }

        let days = Math.floor(timeLeft / (1000 * 60 * 60 * 24));
        let hours = Math.floor((timeLeft % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
        let minutes = Math.floor((timeLeft % (1000 * 60 * 60)) / (1000 * 60));
        let seconds = Math.floor((timeLeft % (1000 * 60)) / 1000);

        document.getElementById("days").innerText = days.toString().padStart(3, '0');
        document.getElementById("hours").innerText = hours.toString().padStart(2, '0');
        document.getElementById("minutes").innerText = minutes.toString().padStart(2, '0');
        document.getElementById("seconds").innerText = seconds.toString().padStart(2, '0');
    }

    let interval = setInterval(updateTimer, 1000);
    updateTimer();
}

// Set the countdown target date (change this to your desired date & time)
let targetDate = new Date("Feb 21, 2025 9:00:00").getTime();

// Start countdown
startCountdown(targetDate);